mapper.select:

select *, DATE_FORMAT(time, '%X-%m-%d') as datetime
from board 
where del_yn = 'N' 

	and type = #{type}

order by no desc
limit #{start}, #{viewRow};


select *, DATE_FORMAT(time, '%X-%m-%d') as datetime
from board
where board.no = #{no};


   	  select count(*) as tot from board 
		where del_yn = 'N' 

			and type = #{type}
